import PropTypes from 'prop-types';
import React from 'react';
import styles from './ProfileStatsItem.module.css';
// import stats from 'user'
// import style from './Profile.module.css'


export default
 function ProfileStatsItem ({props}){
const {label, quantity}=props;
return(
<React.Fragment>
    <li className={styles.statsItem} >
      <span className={styles.label}>Followers</span>
      <span className={styles.quantity}>{stats.followers}</span>
    </li>
    <li className={styles.statsItem} >
      <span className={styles.label}>Views</span>
      <span className={styles.quantity}>{stats.views}</span>
    </li>
    <li className={styles.statsItem} >
      <span className={styles.label}>Likes</span>
      <span className={styles.quantity}>{stats.likes}</span>
    </li>
</React.Fragment>
)
}

ProfileStatsItem.defaultProps = {
    // avatar: "https://images.app.goo.gl/oSbpQwkkWtDxBNMM6",
    // location: "Earth",    
    stats: PropTypes.shape({
        followers: 0,
        views: 0,
        likes: 0,
    })
  };

  ProfileStatsItem.propTypes = {
    followers: PropTypes.number,
    views: PropTypes.number,
    likes: PropTypes.number
  }
